    def homeVideoContent(self):
        rsp = self.fetch("http://43.155.75.36:1069/api.php?do=index_list")
        alist = json.loads(rsp.text)
        alist = alist['list']
        videos = []

        for a in alist:
            videos.append({
                "vod_id":a['id'],
                "vod_name":a['name'],
                "vod_pic":a['pic'],
                "vod_remarks":a['text']
            })
        result = {
            'list':videos
        }
        return result
    def categoryContent(self,tid,pg,filter,extend):
        result = {}
        if 'name' not in extend.keys():
            extend['name'] = tid
        extend['page'] = pg
        filterParams = ["name", "area", "by", "n", "lang", "", "", "", "p", "", "", "year"]
        params = []
        for idx in range(len(filterParams)):
            fp = filterParams[idx]
            if fp in extend.keys():

                params.append(fp + '=' +  extend[fp])
        suffix = '&'.join(params)
        url = 'http://43.155.75.36:1069/api.php?do=class_list&{0}'.format(suffix)
        rsp = self.fetch(url)
        alists = json.loads(rsp.text)
        alist = alists['list']
        videos = []
        for a in alist:
            videos.append({
                "vod_id":a['id'],
                "vod_name":a['name'],
                "vod_pic":a['pic'],
                "vod_remarks":a['progress']
            })

        result['list'] = videos
        result['page'] = pg
        result['pagecount'] = 9999
        result['limit'] = 90
        result['total'] = 999999
        return result
    def detailContent(self,array):
        tid = array[0]
        url = 'http://43.155.75.36:1069/api.php?do=detail_info&id={0}'.format(tid)
        rsp = self.fetch(url)
        alists = json.loads(rsp.text)
        alist = alists['info']
        vod = {
            "vod_id": alist['id'],
            "vod_name": alist['name'],
            "vod_pic": alist['pic'],
            "type_name": alist['vclass'],
            "vod_year": alist['year'],
            "vod_area": alist['area'],
            "vod_remarks": alist['progress'],
            "vod_actor": "",
            "vod_director": "",
            "vod_play_from":alist['form'],
            "vod_play_url":alist['playurl'],
            "vod_content": alist['introduce']
        }

        result = {
            'list':[
                vod
            ]
        }
        return result

    def searchContent(self,key,quick):
        url = 'http://43.155.75.36:1069/api.php?do=search&wd={0}'.format(key)
        # getHeader()
        rsp = self.fetch(url)
        jo = json.loads(rsp.text)
        result = {}
        jArray = []
        if int(jo['count']) > 0:
            for a in jo['list']:
                jArray.append({
                    "vod_id": a['id'],
                    "vod_name": a['name'],
                    "vod_pic": a['pic'],
                    "vod_remarks": a['text']
                })
        result = {
            'list':jArray
        }
        return result
